import './DayBox.scss';

class DayBox extends React.Component {
  constructor(props){
    super(props);

    this.state={
        active: false,
        currentActiveKey: 0,
        dayNumber: 4

    }


  }

  setDayNumber(number){
      this.setState({
          dayNumber: number
      });
  }

  setActive(key){
      this.setState({
       currentActiveKey: key
      });
  }

  toggleActive(){
    this.setState({ active: false });
    const currentState = this.state.active;
    this.setState({ active: !currentState });

}
render() {
    let days = [];
    for(let i=0; i <= this.state.dayNumber; i++){
        let day = <DayBox key={i} onClick={this.setActive} activeKey={this.state.currentActiveKey} />
        console.log('key', i)
        days.push(day);
        console.log('currentActiveKey', this.state.currentActiveKey)
        console.log('key', i)
    }
  return (
    <div className="DayBox" onClick={() => this.setActive() } style={ { backgroundColor: this.state.active ? '#f9fbfe': '#fff'}}>
          <div className="DayTitle">
               <div className="DOW">
            {this.props.dayDatas.Day}
               </div>
           </div>

           <div className="InnerDayBox" >
               <div className="DateRow">
                   {this.props.dayDatas.Date}
               </div>
               <div className="EventRow">
                   {this.props.dayDatas.eventRowText}
               </div>
           </div>
    </div>
  );
 }
}
export default DayBox;
