       let something = {"name": names[y], "contractPrice": prices[i]}
       this.state.jsonObj.push(something)

   }

   return console.log('this.state.jsonObj', this.state.jsonObj)
}


}

makeOneObj(){
    console.log('this.state.contractsAndValues in makeOneObj()ðŸ’¯ ', this.state.contractsAndValues)
    console.log('this.state.allContracts in makeOneObj()ðŸ—ž ', this.state.allContracts)
    console.log('this.state.allContracts.length in makeOneObj()ðŸ“ ', this.state.allContracts.length)
    console.log('this.state.marketContracts in makeOneObj ðŸ‘»', this.state.marketContracts)

    let begin = (this.state.allContracts.length / 2)
    let names = this.state.allContracts[this.props.marketIndex]
    let prices = this.state.contractsAndValues
    this.setState({ currentNames: names})
    this.setState({ currentPrices: prices})

    let together = {'names': names, 'prices': prices}
    console.log('together', together)
    this.state.almostThere.push(together)
    this.setState({ loopThruTheseContracts: this.state.almostThere[0]})
    console.log('this.state.loopThruTheseContracts FROM Almost there', this.state.loopThruTheseContracts)

    return this.combinedObj();




}

 combinedObj(){
  console.log('hello')
}

renderIt(){

    this.state.jsonObj.map((row, x) => {
        console.log('row.name', row.name)
        console.log('x', x)
        return    <LiveContracts 
            key={x}
            contractName={row.name.name}
            contractAvatar={row.name.image}
            contractPrice={row.contractPrice.best_buy_yes_offer}
            contractChange={(row.contractPrice.last_trade_price - row.contractPrice.last_close_price)}

            />

        })
}


render() {



  return (


<div className="parent">
             <div className="one" >
             <button  onClick={() => this.logState()}>Check State</button>

             <button  onClick={() => this.checkMarketContracts()}>Check MarketContracts</button>
             <button  onClick={() => this.checkContractIds()}>Check Contract IDs</button>
             <button  onClick={() => this.checkonlyValues()}>Check Only Values</button>
             </div>

    <SlideToggle
    duration={300}
    collapsed
    render={({
      toggle,
      setCollapsibleElement
    }) => (

        <div className={ toggle ? 'LiveMarketBox' : 'LiveMarketBoxActive'} onClick={toggle}> 

        <div className="LiveMarkets" onClick={() => this.getContractIds()} onChange={toggle}>
        {this.props.contractName}


          </div>
          <SlideToggle >
              <div className="slide-toggle__box" ref={setCollapsibleElement} onChange={() => this.renderIt()}>

             { this.state.jsonObj ?  () => this.showLiveContracts() && this.renderIt() : null

