        if (k !== 0 && k !== 1){
            console.log('k', k)
            console.log('row', row)
            this.state.marketContracts.push(row)

        }
    })
}

checkMarketContracts(){
    console.log('this.state.marketContracts checkMarketContracts()', this.state.marketContracts)
}

getContractIds() {


    let contractRow = this.state.allContracts[this.props.marketIndex]
    contractRow.map((row, y) => {
        console.log('y IN CONTRACT IDS', y)
        console.log('row IN CONTRACT IDS', row.contract_id)
        this.state.contractIds.push(row.contract_id)
    })

    console.log('this.state.loopThruTheseContracts', this.state.loopThruTheseContracts)


    return this.fetchContractPrices();

}

checkContractIds() {
    console.log('this.state.contractIds.length', this.state.contractIds.length)
}

async fetchContractPrices(){

        let contractPrices = [];
        this.setState({valuesLength: this.state.contractIds.count})
        console.log('this.state.valuesLength', this.state.valuesLength)


        for ( var i = 0; i < this.state.contractIds.length; i++){
            const rmpURL = 'https://realmoneypolls.herokuapp.com/api/value/value_list/' + this.state.contractIds[i];
            contractPrices.push(axios.get(rmpURL));
            //this.state.contractsAndValues.push(contractPrices)
            console.log('contractPrices in values list loop', contractPrices)
            console.log('this.state.contractsAndValues in values loop call', this.state.contractsAndValues[0])
            console.log('this.state.contractsAndValues.length', this.state.contractsAndValues.length)

        }

        Promise.all(contractPrices).then((response) => 
        console.log('response', response) +

            response.map((res, index)=> {

                this.state.contractsAndValues.push(res.data[0][0])
                console.log('res.data', res.data[0][0])
                console.log('this.state.contractsAndValues should be 33 and 15', this.state.contractsAndValues)

            } 


            )).catch((err) => console.log(err));

            console.log(this.state.contractsAndValues)
            return this.makeOneObj();


}





showLiveContracts(){
     console.log('hello')
    let names = this.state.currentNames
    console.log('names IN SHOW LIVE CONTRACTS', names)
    let prices = this.state.currentPrices
    console.log('prices IN SHOW LIVE CONTRACTS', prices)




}


multipleCalls() {

}

 makeOneObj(){

    console.log('this.state.contractsAndValues in makeOneObj()üíØ ', this.state.contractsAndValues)
    console.log('this.state.allContracts in makeOneObj()üóû ', this.state.allContracts)
    console.log('this.state.allContracts.length in makeOneObj()üìê ', this.state.allContracts.length)
    console.log('this.state.marketContracts in makeOneObj üëª', this.state.marketContracts)

    let begin = (this.state.allContracts.length / 2)
    let names = this.state.allContracts[this.props.marketIndex]
    let prices = this.state.contractsAndValues
    this.setState({ currentNames: names})
    this.setState({ currentPrices: prices})

    let together = {'names': names, 'prices': prices}
    console.log('together', together)
    this.state.almostThere.push(together)
    this.setState({ loopThruTheseContracts: this.state.almostThere[0]})
    console.log('this.state.loopThruTheseContracts FROM Almost there', this.state.loopThruTheseContracts)

    return;




}


getLoopData () {

    const contracts = this.state.loopThruTheseContracts
    const names =  contracts.names
    const prices = contracts.prices

    const arr = [];
for (var i = 0; i < contracts.names.length; i++){       
    return <LiveContracts   contractName={names[i].name} contractPrice={prices[i].best_buy_yes_offer}

    />

}


}




renderIt(){

    this.state.jsonObj.map((row, x) => {
        console.log('row.name', row.name)
        console.log('x', x)

        return    <LiveContracts 

            contractName={row.name.name}
            contractAvatar={row.name.image}
            contractPrice={row.contractPrice.best_buy_yes_offer}
            contractChange={(row.contractPrice.last_trade_price - row.contractPrice.last_close_price)}

            />

        })
}


render() {

let someshit = this.state.loopThruTheseContracts
console.log('someshit', someshit) 
console.log(this.state.loopThruTheseContracts)

  return (


<div className="parent">
             <div className="one" >
             <button onClick={() => this.getContractIds()} onChange={() => this.showLiveContracts()}> refresh real-time prices </button>

             </div>

    <SlideToggle
    duration={300}
    collapsed
    render={({
      toggle,
      setCollapsibleElement
    }) => (

        <div className={ toggle ? 'LiveMarketBox' : 'LiveMarketBoxActive'} onClick={toggle}> 

        <div className="LiveMarkets" onClick={() => this.getLoopData()} onChange={toggle}>
        {this.props.contractName} 


          </div>
          <SlideToggle >
              <div className="slide-toggle__box" ref={setCollapsibleElement}  >




            {() => this.getLoopData()}




            <MarketBlurb 
            blurb="PredictIt‚Äôs Live Market Data based on upcoming future events. These are the markets most closely associated with key events. Find market details,"
            blurbLink=" here."

            />

        </div>

        </SlideToggle>


      </div>
      )}

      />
      </div>


  )
 }
