import React from 'react';
import logo from './logo.svg';
import './App.css';
import Collapse from "@kunukn/react-collapse"; 
import TitleBar from './components/TitleBar/TitleBar';
import UpcomingHighlights from './components/UpcomingHighlights/UpcomingHighlight';
import PageHeadline from './components/PageHeadline/PageHeadline';
import Calendar from './components/Calendar/Calendar';
import WidgetFooter from './components/WidgetFooter/WidgetFooter';
import SectionHeadline from './components/SectionHeadline/SectionHeadline';
import EventsBar from './components/EventsBar/EventsBar';
import EventObj from './components/EventObj/EventObj';
import SlideToggleContent from './components/SlideToggleContent/SlideToggleContent';
import SlideToggle from 'react-slide-toggle';



const log = console.log.bind(console);
const eases = window.eases;


const ToggleText = () => "Toggle";

const defaultEase = eases["cubicInOut"];

const ProgressBar = ({ progress }) => {
  return (
    <span className="progress-bar">
      <span
        className="progress-bar__inner"
        style={{
          transform: `scaleX(${progress})`
        }}
      />
    </span>
  );
};

class App extends React.Component {
  constructor(props){
    super(props);

    this.state={


    }
  }




render() {




  const eventDatas = [
    { 

      Date: "Nov. 18",
      Headlines: [
        "Fifth Democratic primary debate 🏛 \n\n",
        "Gordon Sondland, Laura Cooper, David Hale Impeachment Hearing 📺 \n\n",
        "Maryland Special Election Filing Deadline 🗳 "
     ],
      SubtitleText: "These events are likely to include live tweeting from @realDonaldTrump",


    },
  { 

    Date: "Nov. 19",
    Headlines: [
      "Jennifer Willaims, Lt. Col. Alexander Vindman, Kurt Volker, Impeachment Hearing 📺 \n\n",
   ],
    SubtitleText: "These events are likely to include live tweeting from @realDonaldTrump",


  },
  { 

    Date: "Nov. 20",
    Headlines: [
      "Fifth Democratic primary debate 🏛 \n\n",
      "Gordon Sondland, Laura Cooper, David Hale Impeachment Hearing 📺 \n\n",
      "Maryland Special Election Filing Deadline 🗳 "
   ],
    SubtitleText: "These events are likely to include live tweeting from @realDonaldTrump",


  },
  { 

    Date: "Nov. 21",
    Headlines: [
      "Fiona Hill Impeachment Hearing 📺",
   ],
    SubtitleText: "These events are likely to include live tweeting from @realDonaldTrump",


  },
  { 

    Date: "Dec. 2",
    Headlines: [
      "Illinois Filing Deadline 🗳 \n\n",
      "North Carolina Filing Deadline 🗳 \n\n",
      "Wisconsin Filing Deadline 🗳 "
   ],
    SubtitleText: "These include major party CD-7 ",


  },
  { 

    Date: "Dec. 9",
    Headlines: [
      "Texas Filing Deadline 🗳 \n\n",
   ],
    SubtitleText: "These include major party CD-7 ",


  },
  { 

    Date: "Dec. 19",
    Headlines: [
      "Sixth & Final Democratic Primary Debate of 2019 🏛 \n\n",
   ],
    SubtitleText: "These events are likely to include live tweeting from @realDonaldTrump ",


  },

  ];


  return (
    <div className="App">
      <TitleBar 
      TitleText="EVENTS CALENDAR"
      SubtitleText="PredictIt Live Markets"
      />
      <UpcomingHighlights
      untilDebate="5 Days"
      untilText="until November debate"
      untilElection="354 Days"
      untilElectionText="until election day"
      />

      <PageHeadline
        Headline="Upcoming Events"
        HeadlineBlurb="PredictIt’s Live Market Data based on upcoming future events. These are the markets most closely associated with key events.  "
      />

      <Calendar


      />

      <SectionHeadline
      SectionHeadline="Associated Events"

      />

      <EventsBar


      />

{
        eventDatas.map((eventData, index) => {

            console.log('this the index', index)
            return <EventObj
            key={index} 
            eventDatas={eventData}
            //toggle={() => ()}          
            />



            })


            } 

<SlideToggle
          duration={1000}
          collapsed
          render={({
            toggle,
            setCollapsibleElement,
            toggleState,
            isMoving,
            hasReversed,
            range,
            progress
          }) => (
            <div
              className={
                "slide-toggle slide-toggle--special " +
                (toggleState || "").toLowerCase()
              }
            >
              <div className="slide-toggle__header">
                <button className="slide-toggle__toggle" onClick={toggle}>
                  <ToggleText />
                </button>
                <ProgressBar progress={progress} />
              </div>
              <div className="slide-toggle__box" ref={setCollapsibleElement}>
                <div
                  className="slide-toggle__box-inner"
                  style={{ opacity: Math.max(0.5, range) }}
                >
                  <p>
                    Default easing is cubicInOut. You can reverse the toggle
                    before the movement completes. Ease in-out works best when
                    reverse toggling is to be used.
                  </p>

                  <p>
                    This should be A11Y friendly, you can test the tabindex by
                    tabbing. The collapsed items should be skipped due to usage
                    of display:none
                  </p>

                  <p>
                    JS animation is used for best animation control and
                    possibility of adding interpolation or using advanged easing
                    configuration which you can't with CSS alone. This triggers
                    browser reflows on every requestAnimationFrame. If you have
                    a very long page this might not be the best option to use.
                  </p>
                  <button onClick={toggle}>
                    <ToggleText />
                  </button>
                </div>
              </div>

              <div className="state-values">
                <div>
                  <span>toggleState:</span>
                  <span>{toggleState}</span>
                </div>
                <div>
                  <span>range:</span>
                  <span>{range.toFixed(1)}</span>
                </div>
                <div>
                  <span>isMoving:</span>
                  <span>{isMoving + ""}</span>
                </div>
                <div>
                  <span>hasReversed:</span>
                  <span>{hasReversed + ""}</span>
                </div>
              </div>
            </div>
          )}
        />






    </div>
  );
 }
}
export default App;
