import React from 'react';
import './LiveMarkets.scss';
import SlideToggle from 'react-slide-toggle';
import LiveContracts from '../LiveContracts/LiveContracts'

class LiveMarkets extends React.Component {
  constructor(props){
    super(props);

    this.state={
        active: false

    }


  }

  toggleActive = () => {

      this.setState({ active: !this.state.active })
  }


render() {
  return (
         <div className="parent">
             <div className="one">

             </div>

    <SlideToggle
    duration={300}
    collapsed
    render={({
      toggle,
      progress,
      setCollapsibleElement,
      range,
    }) => (

        <div className={ !toggle ? 'LiveMarketBox': 'LiveMarketBoxActive'} onClick={this.state.active ? this.setState({ active: !this.state.active}) : null}>

        <div className="LiveMarkets" onClick={this.props.theToggle}>
        {this.props.contractName}

          </div>
          <SlideToggle>
              <div className="slide-toggle__box" ref={setCollapsibleElement}>
        <LiveContracts 
        contractName="Kamala Harris"
        contractLabel="Latest Price"
        contractPrice="0.57" 
        />
        </div>
        </SlideToggle>






      </div>
      )}

      />
      </div>
  );
 }
}
export default LiveMarkets;
