import React from 'react';
import './LiveMarkets.scss';
import SlideToggle from 'react-slide-toggle';
import LiveContracts from '../LiveContracts/LiveContracts'
import MarketBlurb from '../MarketBlurb/MarketBlurb'
import axios from 'axios';
import { objectTypeSpreadProperty, nullLiteral } from '@babel/types';


class LiveMarkets extends React.Component {
  constructor(props){
    super(props);

    this.state={
        contracts: [],
        contractNames: [],
        contractIds: [],
        contractPrices: [],



    };

    this.createContracts = this.createContracts.bind(this);
    this.componentDidMount = this.componentDidMount.bind(this);


  }

  globalContracts = [];
  globalContractIds = [];
  globalNames = [];
  globalArray = [];
  bigObj= [];


  valuesLoop(){
    // check contractIds array -- see if they're all there
    console.log('this.globalContractIds -- BEFORE valuesLoop() begins...', this.globalContractIds);
    console.log('this.globalArray -- BEFORE valuesLoop() begins...', this.globalArray);
    // checked!

    const idsArray = this.globalContractIds
    this.values = idsArray.forEach((array, index) => {
        if (this.globalArray.length <= 1 ){

        console.log('array', array)

        axios.get('http://localhost:3002/api/value/value_list/' + array).then(res =>{

        const priceGroupings = {"contractPrice": res.data[0][0].best_buy_yes_offer, "contractChange": (res.data[0][0].last_trade_price - res.data[0][0].last_close_price).toFixed(2)}
        this.globalArray.push(priceGroupings)

        this.createContracts()


     return;


    }) 
    // end of prices call
    } // if statement
    });
    }



  async componentDidMount() {


      //Get contracts
   axios.get('http://localhost:3002/api/contract/contract_list/'+ this.props.mId1Title).then(res => { 
        // this.setState({ contracts: res.data[0] });
        console.log('res.data[0] NO IDs in Global Contracts', res.data[0])
        this.globalContracts.push(res.data[0])
        const contracts = this.globalContracts
        console.log('contracts[0] how many b4 i run this loop?', contracts[0])

        contracts[0].map((contract, index) => {

            console.log('this.globalContracts BEFORE ADDING TO ARRAY', this.globalContracts)
            console.log('this.globalContractIds BEFORE ADDING TO ARRAY', this.globalContractIds)
             this.globalContractIds.push(contract.contract_id)
             const namesUrls = {"name": contract.name, "url": contract.image }
             this.globalNames.push(namesUrls)


        }
        )
        this.setState({ contractIds : this.globalContractIds})
        return ;

    }
    );

    }



  toggleActive = () => {

      this.setState({ active: !this.state.active })
  }


  createContracts(){

    setTimeout(5500)

    //console.log('this.globalArray prices -- WITHIN createContracts()', this.globalArray)
    //console.log('this.globalContracts --  WITHIN createContracts()', this.globalContracts)
    //console.log('this.globalNames --  WITHIN createContracts()', this.globalNames)



    if (this.globalNames.length === this.globalArray.length){this.globalNames.map((row, index)=>{
        this.globalArray.map((line, k) => {
            if(k === index){
                const jointObj = {"name": row.name, "url": row.url, "contractPrice": line.contractPrice, "contractChange": line.contractChange}
                if (this.bigObj.length === this.globalNames.length){
                    console.log('im done....total rows =' + this.bigObj.length);
                }
                this.bigObj.push(jointObj);
                console.log('this.bogObj', this.bigObj);
            } else {

            }
        })
        return;
    })
} else {

    }

  }






render() {


  return (
         <div className="parent">
             <div className="one">

             </div>

    <SlideToggle
    duration={300}
    collapsed
    render={({
      toggle,
      progress,
      setCollapsibleElement,
      range,
    }) => (

        <div className={ toggle ? 'LiveMarketBox' : 'LiveMarketBoxActive'} onClick={toggle} onChange={this.valuesLoop()} >

        <div className="LiveMarkets"  onClick={this.props.theToggle} >
        {this.props.contractName}

          </div>
          <SlideToggle >
              <div className="slide-toggle__box" ref={setCollapsibleElement}>


               {

               this.bigObj.map((obj, index) => {
                const name = obj.name;
                const url = obj.url;
                const price = obj.contractPrice;
                const change = obj.contractChange;
                return (

                            <LiveContracts
                            key={index}
                            contractName={name}
                            contractAvatar={url}
                            contractPrice={price}
                            contractChange={change}


                            />
                )


               })
               }



            <MarketBlurb 
            blurb="PredictItâ€™s Live Market Data based on upcoming future events. These are the markets most closely associated with key events. Find market details,"
            blurbLink=" here."

            />

        </div>

        </SlideToggle>








      </div>
      )}

      />
      </div>
  );
 }
}
export default LiveMarkets;
