import './Calendar.scss';
import './DayBox/DayBox.js'
import DayBox from './DayBox/DayBox.js';

class Calendar extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      active: false,
      selectedDayId: 6,


    }
  };


    getInitialState() {
      this.setState({ selectedDayId: 1 }) 
    }

    isActive(id) {
        return this.state.selectedTabId === id; 
    }

    setActiveDay(selectedDayId) {
        console.log('clicking on', selectedDayId)
      this.setState({ selectedDayId: selectedDayId });
    }





  toggleClass() {
      const currentState = this.state.active;
      this.setState({
          active: !currentState
      });
  }

  getClassName() {
    if(this.state.selectedDayId === this){
        return "DayBoxActive" 
    }
    else return 'DayBox'
  }


render() {


    const dayDatas = [
        { 

        Date: "18",
        eventRowText: "Jennifer Willaims, Lt. Col. Alexander Vindman, Kurt Volker, Impeachment Hearing ðŸ“º ",
        Day: "MONDAY",
        id: 1,

      },
        { 

        Date: "19",
        eventRowText: "Jennifer Willaims, Lt. Col. Alexander Vindman, Kurt Volker, Impeachment Hearing ðŸ“º ",
        Day: "TUESDAY",
        id: 2,

      },
        { 

        Date: "20",
        eventRowText: "Fifth Dem Debate ðŸ“º, Union Leader Endorsement ðŸ—ž ",
        Day: "WEDNESDAY",
        id: 3,

      },
        { 

        Date: "21",
        eventRowText: "Fiona Hill Impeachment Hearing ðŸ“º",
        Day: "THURSDAY",
        id: 4,

      },
        { 

        Date: "22",
        eventRowText: "Illinois Filing Deadline ðŸ—³",
        Day: "FRIDAY",
        id: 5,

      }

      ];
  return (

    <div className="main">

    <div className="CalendarBox">
    {
        dayDatas.map((dayData, index) => {
            return <DayBox
            key={index} 
            dayDatas={dayData}
            isActive={false}
            onActiveDay={ this.state.selectedDayId}
            onClick={() => this.setActiveDay(index)}
            className={ () => this.getClassName}
            />


            })
            } 


    </div>


    <div className="Footer">

    </div>


    </div>
  );
 }
}
export default Calendar;
