import React from 'react';
import './DayBox.scss';

class DayBox extends React.Component {
  constructor(props){
    super(props);

    this.state={
        active: false,

    }

    toggleActive = () => {
        const currentState = this.state.active;
        this.setState({ active: !currentState });

    }
  }
render() {
  return (
    <div className="DayBox" onClick={() => this.toggleActive() } style={ { backgroundColor: this.state.active ? '#f9fbfe': '#fff'}}>
          <div className="DayTitle">
               <div className="DOW">
            {this.props.Day}
               </div>
           </div>

           <div className="InnerDayBox" >
               <div className="DateRow">
                   {this.props.Date}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText}
               </div>
           </div>
    </div>
  );
 }
}
export default DayBox;
