            contractName: "Joe Biden",
            contractLabel: "Latest Price",
            contractPrice: "0.01",
            priceChange: "0.02"
        },
        {
            avatar: require("../../images/BernieAvatar.png"),
            contractName: "Bernie Sanders",
            contractLabel: "Latest Price",
            contractPrice: "0.01",
            priceChange: "0.02"
        },

    ]



  return (
         <div className="parent">
             <div className="one">

             </div>

    <SlideToggle
    duration={300}
    collapsed
    render={({
      toggle,
      progress,
      setCollapsibleElement,
      range,
    }) => (

        <div className={ toggle ? 'LiveMarketBoxActive' : 'LiveMarketBoxActive'} onClick={toggle }>

        <div className="LiveMarkets" onClick={this.props.theToggle}>
        {this.props.contractName}

          </div>
          <SlideToggle>
              <div className="slide-toggle__box" ref={setCollapsibleElement}>
              { 

      activeContracts.map((activeContract, index) => {

          console.log('this the index', index)
