import React from 'react';
import './Calendar.scss';

class Calendar extends React.Component {
  constructor(props){
    super(props);

    this.state={
      currentColor: "#fff",
      innerBoxColor: this.props.innerBoxColor,

    }
  }



  handleBgColorToggle (){
    if (this.state.currentColor === '#fff')
    {
    this.setState({
        currentColor: '#f9fbfe'
      });
    }
    this.setState({
        currentColor: '#fff'
    })


  }

render() {
  return (

    <div className="main">

    <div className="CalendarBox">
       <div className="DayBox">
           <div className="DayTitle">
               <div className="DOW">
            {this.props.Day0}
               </div>
           </div>
           <div className="InnerDayBox">
               <div className="DateRow">
                   {this.props.firstDate}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText0}
               </div>
           </div>
       </div>
       <div className="DayBox">
       <div className="DayTitle">
               <div className="DOW">
            {this.props.Day1}
               </div>
           </div>
           <div className="InnerDayBox">
               <div className="DateRow">
               {this.props.secondDate}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText1}
               </div>
           </div>
       </div>

       <div className="DayBox">
       <div className="DayTitle">
               <div className="DOW">
            {this.props.Day2}
               </div>
           </div>
           <div className="InnerDayBox">
               <div className="DateRow">
               {this.props.thirdDate}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText2}
               </div>
           </div>
       </div>

       <div className="DayBox">
       <div className="DayTitle">
               <div className="DOW">
            {this.props.Day3}
               </div>
           </div>
           <div className="InnerDayBox">
               <div className="DateRow">
               {this.props.fourthDate}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText3}
               </div>
           </div>
       </div>

       <div className="DayBox">
       <div className="DayTitle">
               <div className="DOW">
            {this.props.Day4}
               </div>
           </div>
           <div className="InnerDayBox">
               <div className="DateRow">
               {this.props.fifthDate}
               </div>
               <div className="EventRow">
                   {this.props.eventRowText4}
               </div>
           </div>
       </div>

    </div>


    <div className="Footer">

    </div>


    </div>
  );
 }
}
export default Calendar;
