import React from 'react';
import './Calendar.scss';
import './DayBox/DayBox.js'
import DayBox from './DayBox/DayBox.js';

class Calendar extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      active: false,
      currentColor: "#fff",
      calendarData : [
        {
          dayofWeek: "1",
          bgColor: "#fff",

        },
        {
            dayofWeek: "2",
            bgColor: "#fff",

        },
        {
            dayofWeek: "3",
            bgColor: "#fff",
        },
        {
            dayofWeek: "4",
            bgColor: "#fff",
        },
        {
            dayofWeek: "5",
            bgColor: "#fff",
        },
      ],

    }
  }


  toggleClass() {
      const currentState = this.state.active;
      this.setState({
          active: !currentState
      });
  }


  handleBgColorToggle = () => {
    if (this.state.currentColor === '#fff')
    {
    this.setState({
        currentColor: '#f9fbfe'
      });
    }
    this.setState({
        currentColor: '#fff'
    })



    this.setState({
        calendarData : [
            {
              dayofWeek: "1",
              bgColor: "#fff",

            },
            {
                dayofWeek: "2",
                bgColor: "#fff",

            },
            {
                dayofWeek: "3",
                bgColor: "#fff",
            },
            {
                dayofWeek: "4",
                bgColor: "#fff",
            },
            {
                dayofWeek: "5",
                bgColor: "#fff",
            },
          ],
    })


  }

  toggleBgColors() {



    console.log('1st Console Log bgColor', this.state.calendarData)

    this.setState({
        calendarData : [
            {
              dayofWeek: "1",
              bgColor: "#f9fbfe",

            },
          ],
    })

    if (this.state.calendarData[0].bgColor !== '#fff'){
        this.setState({
            calendarData: this.state.currentColor,
        })
    }

    console.log('I just set this', this.state.calendarData[0])



  }


render() {
    const dayDatas = [
        { 

        Date: "18",
        eventRowText: "Jennifer Willaims, Lt. Col. Alexander Vindman, Kurt Volker, Impeachment Hearing ðŸ“º ",
        Day: "MONDAY",

      },
        { 

        Date: "19",
        eventRowText: "Jennifer Willaims, Lt. Col. Alexander Vindman, Kurt Volker, Impeachment Hearing ðŸ“º ",
        Day: "TUESDAY",

      },
        { 

        Date: "20",
        eventRowText: "Fifth Dem Debate ðŸ“º, Union Leader Endorsement ðŸ—ž ",
        Day: "WEDNESDAY",

      },
        { 

        Date: "21",
        eventRowText: "Fiona Hill Impeachment Hearing ðŸ“º",
        Day: "THURSDAY",

      },
        { 

        Date: "22",
        eventRowText: "Illinois Filing Deadline ðŸ—³",
        Day: "FRIDAY",

      }

      ];
  return (

    <div className="main">

    <div className="CalendarBox">
    {
        dayDatas.map((dayData, index) => {
            return <DayBox
            key={index} 
            dayData={dayData}
            />


            })
            } 


    </div>


    <div className="Footer">

    </div>


    </div>
  );
 }
}
export default Calendar;
